import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import UpdateForm from "../components/UpdateForm";

function BlogProfile() {
  const { id } = useParams();
  const [blog, setBlog] = useState([]);
  const [selectedBlog, setSelectedBlog] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = () => {
    axios.get(`http://localhost:3001/blogs/${id}`).then((res) => {
      setBlog(res.data);
      console.log(res.data);
    });
  };

  const handleUpdateClick = () => {
    setSelectedBlog(blog);
  };

  const handleUpdateDone = () => {
    setSelectedBlog(null);
    fetchBlogs(); // Fetch updated data after update
  };

  return (
    <div className="container-fluid bg-secondary text-center p-6">
      <h3 className="text-2xl p-4">BLOG PROFILE</h3>
      <div className="relative">
        {selectedBlog === blog && (
          <UpdateForm
            id={id}
            InitialTitle={blog.title}
            InitialPreview={blog.preview}
            InitialPost={blog.post}
            onUpdate={handleUpdateDone}
          />
        )}
        <h4 className="text-xl p-5">Title: {blog.title}</h4>
        <br />
        <br />
        <span>Preview: {blog.preview}</span>
        <br />
        <br />
        <span className="p-4">Created: {blog.createdAt}</span>
        <br />
        <br />
        {/* <p className="">{blog.post}</p>
         */}
        <p
          className=""
          dangerouslySetInnerHTML={{
            __html: blog.post.replace(/\n/g, "<br />"),
          }}
        ></p>

        <div>
          <button
            className="absolute top-0 left-0 w-[130px] h-[50px] border border-black hover:bg-teal-700 hover:text-white"
            onClick={handleUpdateClick}
          >
            Update Blog
          </button>
        </div>
      </div>

      <div class="container px-4 py-5" id="hanging-icons">
        <h2 class="pb-2 border-bottom">Hanging icons</h2>
        <div class="row g-4 py-5 row-cols-1 row-cols-lg-3">
          <div class="col d-flex align-items-start">
            <div class="icon-square text-body-emphasis bg-body-secondary d-inline-flex align-items-center justify-content-center fs-4 flex-shrink-0 me-3"></div>
            <div>
              <h3 class="fs-2 text-body-emphasis">Featured title</h3>
              <p>
                Paragraph of text beneath the heading to explain the heading.
                We'll add onto it with another sentence and probably just keep
                going until we run out of words.
              </p>
              <a href="#" class="btn btn-primary">
                Primary button
              </a>
            </div>
          </div>
          <div class="col d-flex align-items-start">
            <div class="icon-square text-body-emphasis bg-body-secondary d-inline-flex align-items-center justify-content-center fs-4 flex-shrink-0 me-3"></div>
            <div>
              <h3 class="fs-2 text-body-emphasis">Featured title</h3>
              <p>
                Paragraph of text beneath the heading to explain the heading.
                We'll add onto it with another sentence and probably just keep
                going until we run out of words.
              </p>
              <a href="#" class="btn btn-primary">
                Primary button
              </a>
            </div>
          </div>
          <div class="col d-flex align-items-start">
            <div class="icon-square text-body-emphasis bg-body-secondary d-inline-flex align-items-center justify-content-center fs-4 flex-shrink-0 me-3"></div>
            <div>
              <h3 class="fs-2 text-body-emphasis">Featured title</h3>
              <p>
                Paragraph of text beneath the heading to explain the heading.
                We'll add onto it with another sentence and probably just keep
                going until we run out of words.
              </p>
              <a href="#" class="btn btn-primary">
                Primary button
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BlogProfile;
